// App.js
import React, { useState } from "react";
import AddTodo from "./AddTodo";
import TodoList from "./TodoList";
import EditTodo from "./EditTodo";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const App = () => {
  const [editingTodo, setEditingTodo] = useState(null);

  const handleDeleteSuccess = () => {
    toast.success("Todo deleted successfully!");
  };

  const handleUpdateSuccess = () => {
    toast.success("Todo updated successfully!");
  };

  const handleDeleteError = () => {
    toast.error("Failed to delete todo. Please try again.");
  };

  const handleUpdateError = () => {
    toast.error("Failed to update todo. Please try again.");
  };

  const handleEdit = (todo) => {
    setEditingTodo(todo);
  };

  const handleCancelEdit = () => {
    setEditingTodo(null);
  };

  return (
    <div style={appContainerStyle}>
      <h2 style={appHeaderStyle}>MERN Todo App with GraphQL</h2>
      <AddTodo />
      <TodoList handleEdit={handleEdit} handleDelete={handleDelete} />
      {editingTodo && (
        <EditTodo
          todo={editingTodo}
          onCancel={handleCancelEdit}
          onSave={handleSaveEdit}
          handleUpdateSuccess={handleUpdateSuccess}
        />
      )}
      <ToastContainer position="bottom-right" autoClose={3000} />
    </div>
  );
};

// Styles and other constants...

// export default App;

// Styles
const appContainerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

const appHeaderStyle = {
  color: "#333",
};

const addTodoContainerStyle = {
  margin: "20px 0",
};

const addTodoInputStyle = {
  padding: "8px",
  marginRight: "8px",
};

const addTodoButtonStyle = {
  padding: "8px",
  backgroundColor: "#5cb85c",
  color: "white",
  border: "none",
  cursor: "pointer",
};

const editButtonStyle = {
  backgroundColor: "#5bc0de",
  color: "white",
  padding: "8px",
  marginRight: "4px",
  border: "none",
  cursor: "pointer",
};

const deleteButtonStyle = {
  backgroundColor: "#d9534f",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};
const todoStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  padding: "8px",
  borderBottom: "1px solid #ddd",
};

const destroyButtonStyle = {
  backgroundColor: "transparent",
  color: "#d9534f",
  border: "none",
  cursor: "pointer",
};
const editTodoContainerStyle = {
  display: "flex",
  alignItems: "center",
  marginTop: "8px",
};

const editTodoInputStyle = {
  flex: "1",
  padding: "8px",
  marginRight: "8px",
};

const saveButtonStyle = {
  backgroundColor: "#5cb85c",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};

const cancelButtonStyle = {
  backgroundColor: "#d9534f",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};

export default App;
