// src/styles.js

// Common styles and constants
export const appContainerStyle = {
  textAlign: "center",
  fontFamily: "Arial, sans-serif",
};

export const appHeaderStyle = {
  color: "#333",
};

export const addTodoContainerStyle = {
  margin: "20px 0",
};

export const addTodoInputStyle = {
  padding: "8px",
  marginRight: "8px",
};

export const addTodoButtonStyle = {
  padding: "8px",
  backgroundColor: "#5cb85c",
  color: "white",
  border: "none",
  cursor: "pointer",
};

export const editButtonStyle = {
  backgroundColor: "#5bc0de",
  color: "white",
  padding: "8px",
  marginRight: "4px",
  border: "none",
  cursor: "pointer",
};

export const deleteButtonStyle = {
  backgroundColor: "#d9534f",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};

export const todoStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  padding: "8px",
  borderBottom: "1px solid #ddd",
};

export const destroyButtonStyle = {
  backgroundColor: "transparent",
  color: "#d9534f",
  border: "none",
  cursor: "pointer",
};

export const editTodoContainerStyle = {
  display: "flex",
  alignItems: "center",
  marginTop: "8px",
};

export const editTodoInputStyle = {
  flex: "1",
  padding: "8px",
  marginRight: "8px",
};

export const saveButtonStyle = {
  backgroundColor: "#5cb85c",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};

export const cancelButtonStyle = {
  backgroundColor: "#d9534f",
  color: "white",
  padding: "8px",
  border: "none",
  cursor: "pointer",
};
